package Game;

class Beat extends Threads { // 음악 스레드
	String song1 = "";

	@Override
	public void run() {
		try {
			sleep(1000);
			Start();
		} catch (InterruptedException e1) {
			e1.printStackTrace();
		}
		for (int i = 0; i < song1.length(); i++) {
			try {
				if (BitTetris.Gamestate == 2)
					i = 0;
				if (song1.charAt(i) == '1') {
					BitTetris.drop = true;
				} else {
					BitTetris.drop = false;
				}
				Thread.sleep(250);
			} catch (InterruptedException e) {
				Resume();
			}
		}
		System.out.println("\n아쉽게도 준비한건 여기까지입니다");
	}
}